import React, { useEffect, useState } from 'react';
import { Container, Typography, List, ListItem, ListItemText, Button, Box } from '@mui/material';
import api, { adoptions } from '../utils/api';
import { toast } from 'react-toastify';

const Notifications = () => {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    fetchNotes();
  }, []);

  const fetchNotes = async () => {
    try {
      const { data } = await api.get('/notifications');
      setNotes(data);
    } catch (err) {
      toast.error('Failed to fetch notifications');
    }
  };

  const handleAction = async (adoptionId, action, noteId) => {
    try {
      await adoptions.updateStatus(adoptionId, action);
      // mark notification as read
      await api.patch(`/notifications/${noteId}/read`);
      toast.success(`Request ${action}`);
      fetchNotes();
      // optionally refresh other lists in the app
    } catch (err) {
      toast.error('Failed to perform action');
    }
  };

  return (
    <Container>
      <Typography variant="h4" sx={{ my: 2 }}>Notifications</Typography>
      <List>
        {notes.map(n => (
          <ListItem key={n._id} divider>
            <ListItemText primary={n.message} secondary={new Date(n.createdAt).toLocaleString()} />
            {/* If this notification references an adoption, show accept/reject for owners */}
            {n.adoption && (
              <Box sx={{ display: 'flex', gap: 1 }}>
                <Button color="success" variant="contained" onClick={() => handleAction(n.adoption, 'approved', n._id)}>Accept</Button>
                <Button color="error" variant="outlined" onClick={() => handleAction(n.adoption, 'rejected', n._id)}>Reject</Button>
              </Box>
            )}
          </ListItem>
        ))}
      </List>
    </Container>
  );
};

export default Notifications;
